function DrawcardConfig.GetConfig(key)
	local tb = DrawcardConfig.Values[key]
	if not tb then
		print_error(key)
		return nil
	end
	local config = {["groupid"] = 1,["groupname"] = 2,["groupdes"] = 3,["value_gold"] = 4,["value_item"] = 5,["sort"] = 6,["securityway"] = 7,["recruittype"] = 8,["bgpic"] = 9,["banner"] = 10,["queuepic"] = 11,["label"] = 12,["labellocation"] = 13,["recruitweight"] = 14,["previewlocation"] = 15,["security"] = 16,["guaranteetime"] = 17,["guaranteerate"] = 18,["nazo"] = 19,}
	for k,v in pairs(config) do
		config[k] = tb[v]
	end
	return config 
end
return DrawcardConfig