	end
	local config = {["id"] = 1,["name"] = 2,["desc"] = 3,["displaytype"] = 4,["type"] = 5,["quality"] = 6,["sortid"] = 7,["needlevel"] = 8,["gainway"] = 9,["cantips"] = 10,["resolve"] = 11,["canuse"] = 12,["extinctiontime"] = 13,["icon"] = 14,["icon_small"] = 15,["icon_small_2"] = 16,["stroyauthor"] = 17,["story"] = 18,["sale"] = 19,["dropid"] = 20,["giftid"] = 21,["value"] = 22,["funcdesc"] = 23,["jump_icon"] = 24,["nazo"] = 25,}
	for k,v in pairs(config) do
		config[k] = tb[v]
	end
	return config 
end
return ItemConfig